	public float getSeaLevelPrediction(float y,float m, float d){
		float yearDecimal;
		float sea_level_rise;
		int i;
		for(i=0;i<m-1;i++) d+=month_day[i];
		yearDecimal=y+(float)d/365.0f;

		sea_level_rise=yintercept + (slope * yearDecimal) + 41.15f;
		return sea_level_rise;//yintercept + (slope * yearDecimal);
	}

